  SampleDriver& operator=(const SampleDriver& rhs);
  int operator==(const SampleDriver& rhs);	    
  int operator!=(const SampleDriver& rhs);	    


public:
  virtual void onAttach(CControlModule& configuration); 
  virtual void Initialize(CCCUSB& camac);	            
  virtual std::string Update(CCCUSB& camac);            
  virtual std::string Set(CCCUSB& camac,                
			  std::string parameter, 
			  std::string value);              
  virtual std::string Get(CCCUSB& camac,                
			  std::string parameter);          
  virtual void clone(const CControlHardware& rhs);	    

};

void
SampleDriver::Initialize(CCCUSB& camac)
{
}

std::string
SampleDriver::Update(CCCUSB& camac)
{
    return "OK";
}

std::string
SampleDriver::Set(CCCUSB& camac, std::string parameter, std::string value)
{
  try {
    m_pConfig->configure(parameter, value);   
  }
  catch(std::string msg) {
    std::string error = "ERROR ";             
    error += msg;

    return error;
  }
  return "OK";                                 
}

std::string
SampleDriver::Get(CCCUSB& camac, std::string parameter)
{
  try {
    return m_pConfig->cget(parameter);    
  } catch(std::string msg) {
    std::string error = "ERROR - ";       
    error += msg;
    return error;
  }

}

void
SampleDriver::clone(const CControlHardware& rhs)
{
}

class SampleCreator : public CModuleCreator {     
public:
  CControlHardware* operator()(std::string name);
};

CControlHardware*
SampleCreator::operator()(std::string name)
{
  return new SampleDriver(name);                
}

extern "C" {                                 
int
Sampledriver_Init(Tcl_Interp* pInterp)       
{
  int status;

  status = Tcl_PkgProvide(pInterp, "Sampledriver", "1.0");    
  if (status != TCL_OK) {                   
    return status;
  }

  CModuleFactory* pFact = CModuleFactory::instance(); 
  pFact->addCreator("sample", new SampleCreator);     

  return TCL_OK;
}
}

package require cccusb             
snit::type SampleDriver {          
    option -anint   -default 0    -configuremethod _CheckInt
    option -anintlist -default 0  -configuremethod _CheckIntList 
    option -abool   -default true -configuremethod _CheckBool


    construtor args {}             
    method Initialize crate {}     
    method Update crate {}         
    method Set {crate what value} {} 
    method Get {crate what}  {}      

    method _CheckInt     {name value} {}
    method _CheckIntList {name value} {}
    method _CheckBool    {name value} {}
}

construtor args {
    $self configurelist $args
}

method Update crate {
    error "This device does not support 'Update'"
}

method Set {crate what value} {
    $self configure $what $value
    return "OK"
}

method Get {crate what} {
    if {[array names options $what] ne ""} {
        return $options($what)
    } else {
        error "No such parameter '$what'"
    }
}

method _CheckInt     {name value} {
    $self _IsInt $value
    set options($name) $value
}

method _CheckIntList {name value} {
    if {[string is list $value]} {
        if {[llength $value] == 16} {
            foreach element $value {
                $self _IsInt $element
            }
            set options($name) $value
        } else {
            error "$name option expects a list exactly 16 elements long"
        }
    } else {
        error "$name must be a valid Tcl list"
    }
}
method _CheckBool    {name value} {
    if {[string is boolean -strict $value]} {
        set options($name) $value
    } else {
        error "$name must be a boolean parameter, was: $value"
    }
}
method _IsInt        {value} {
    if {![string is integer -strict $value]} {
        error "Configuration value $value must be an integer and is not"
    }
}

listitem.para:
The pValues parameter should be a pointer to a null terminated set of character pointers. This sounds much harder than it is:
const char* enumValues[] = {"red", "green", "blue", NULL};
In the code fragment above, enumValues can be passed in as the pValues argument and will constrain the values of the configuration parameter to be in the set {red, greeen, blue}.

