void
CTemplateDriver::Initialize(CVMUSB& controller)
{

  uint32_t base = m_pConfiguration->getUnsignedParameter("-base");


}

void
CTemplateDriver::addReadoutList(CVMUSBReadoutList& list)
{

  uint32_t base  = m_pConfiguration->getUnsignedParameter("-base"); 
  int      id    = m_pConfiguration->getIntegerParameter("-id"); 

  list.addMarker(id);                                            
}

